import java.util.Scanner;public class OrtalamaHesaplama {    public static void main(String[] args) {        double mat,fizik,turkce,kimya,muzik,derssayisi=0,nottoplami=0;        Scanner inp=new Scanner(System.in);        System.out.print("Matematik notunuz :");        mat=inp.nextInt();        if (mat>=0&& mat<=100){            derssayisi++;            nottoplami+=mat;        }else{            derssayisi+=0;            nottoplami+=0;        }        System.out.print("Fizik notunuz :");        fizik=inp.nextInt();        if (fizik>=0&& fizik<=100){            derssayisi++;            nottoplami+=fizik;        }else{            derssayisi+=0;            nottoplami+=0;        }        System.out.print("Türkçe notunuz :");        turkce=inp.nextInt();        if (turkce>=0&& turkce<=100){            derssayisi++;            nottoplami+=turkce;        }else{            derssayisi+=0;            nottoplami+=0;        }        System.out.print("Kimya Notunuz :");        kimya=inp.nextInt();        if (kimya>=0&& kimya<=100){            derssayisi++;            nottoplami+=mat;        }else{            derssayisi+=0;            nottoplami+=0;        }        System.out.print("Müzik Notunuz :");        muzik=inp.nextInt();        if (muzik>=0&& muzik<=100){            derssayisi++;            nottoplami+=muzik;        }else{            derssayisi+=0;            nottoplami+=0;        }        double ortalama=nottoplami/derssayisi;        if (ortalama>=55){            System.out.println("Tebrikler sınıfı geçtiniz...");            System.out.println("Ortalamanız : "+" "+ortalama);        }else{            System.out.println("Ortalamanız geçer notun altında.Sınıfta kaldınız.");            System.out.println("Ortalamanız : " +" "+ortalama);        }    }}